<script>
import { store } from './store'
import MapContainer from './components/MapContainer'
import FullPageContainer from './components/FullPageContainer'
import Tooltip from './components/Tooltip'
import StyleModal from './components/StyleModal'
import Bloom from './components/Bloom'
import BarChart from './components/BarChart'
import ImageAndCaption from './components/ImageAndCaption'
import SoundText from './components/SoundText'
import {json as getJSON} from 'd3-request'
import dataFile from '../static/data.json'
import peopleFile from '../static/people.json'

export default {
  name: 'app',
  store: store,
  components: {
    MapContainer,
    FullPageContainer,
    BarChart,
    Bloom,
    Tooltip,
    SoundText,
    StyleModal,
    ImageAndCaption
  },
  data () {
    return {
      theData: []
    }
  },
  created: function () {
    getJSON(dataFile, (resp) => {
      this.$store.commit('setData', resp)
      this.theData = resp
    })
    getJSON(peopleFile, (resp) => {
      this.$store.commit('setPeopleData', resp)
    })
    console.log('I, too by Langston Hughes')
    console.log('I, too, sing America.\nI am the darker brother. \nThey send me to eat in the kitchen \nWhen company comes, \nBut I laugh, \nAnd eat well, \nAnd grow strong. \n\nTomorrow, \nI’ll be at the table \nWhen company comes. \nNobody’ll dare \nSay to me, \n“Eat in the kitchen,” \nThen. \n\nBesides, \nThey’ll see how beautiful I am \nAnd be ashamed— \n\nI, too, am America.')
  }
}
</script>
